mapper.insert:
  
		        insert into salesDepartmentInfo (salesDepartmentName, salesDepartmentAddress, 
			      salesDepartmentPhone, parentID,level, isDelete
			      )
			    values (#{salesDepartmentName,jdbcType=VARCHAR}, #{salesDepartmentAddress,jdbcType=VARCHAR}, 
			      #{salesDepartmentPhone,jdbcType=VARCHAR}, #{parentID,jdbcType=INTEGER},#{level,jdbcType=INTEGER}, #{isDelete,jdbcType=INTEGER}
			      )

mapper.update:
  
		        
		        update salesDepartmentInfo
			        
			            
			                 
			                     
							        when id=#{item.id} then #{item.salesDepartmentName,jdbcType=VARCHAR}
							      
			                 
			            
			            
			            
			                 
			                     
							        when id=#{item.id} then #{item.salesDepartmentAddress,jdbcType=VARCHAR}
							      
			                 
			            
			            
			            
			                 
			                     
							        when id=#{item.id} then #{item.salesDepartmentPhone,jdbcType=VARCHAR}
							      
			                 
			            
			            
			            
			                 
			                     
							        when id=#{item.id} then #{item.parentID,jdbcType=INTEGER}
							      
			                 
			            
			            
			            
			                 
			                     
							        when id=#{item.id} then #{item.level,jdbcType=INTEGER}
							      
			                 
			            		
			            
			             
			                 
			                     
							        when id=#{item.id} then #{item.isDelete,jdbcType=INTEGER}
							      
			                 
			            			            
			            
			        			        
			        where id in
			        
			            #{item.id,jdbcType=INTEGER}

mapper.select:
  
		        select count(*) from salesDepartmentInfo e   
		        
				  	
				        ${key} = #{value} 
				     
	               
	              and isDelete=0
