mapper.select:
select (@j :=@j + 1) AS No,f.number,f.package,f.pname,f.salesDepartmentName from ( select a.orderCode, a.customerType, a.customerPhone, a.customerName, d.salesDepartmentName as pname, a.customerAddress, a.orderDate, CONCAT(YEAR(a.orderDate),'-',DATE_FORMAT(a.orderDate,'%m')) as months , a.orderAmount, a.healthMember, a.customerSign, a.salesDepartmentID, a.isDelete, b.level, b.salesDepartmentName, b.salesDepartmentPhone, COUNT(a.customerPhone) as number, cast((c.receiptAmount/249) as decimal(18,0)) as package, (@i :=@i + 1) AS No from orderinfo a left join salesdepartmentInfo b on a.salesDepartmentID = b.id left join receiptinfo c on a.salesDepartmentID = c.salesDepartmentID left join salesdepartmentInfo d on d.id = b.parentID, (SELECT @i := 0) AS it where 1 = 1 and a.isDelete=0
and b.level = #{level}
and a.salesDepartmentID = #{salesDepartmentID}
and a.orderDate like concat(#{yearmonth},'%')
GROUP BY b.salesDepartmentPhone, b.salesDepartmentName, months order BY number desc,package desc ) f, (SELECT @j := 0) AS its order BY number desc,package desc
